                        style={isActive(history, "/signin")}
                        to="/signin"
                    >
                        Signin/
                    </Link>

                    <Link
                        //className="nav-link"
                        style={isActive(history, "/signup")}
                        to="/signup"
                    >
                        Signup
                    </Link>
                </Fragment>
            )}
            </li>

            {isAuthenticated() && (
                <li className="nav-item">
                    <span
                        className="nav-link"
                        style={{ cursor: "pointer", color: "#ffffff" }}
                        onClick={() =>
                            signout(() => {
                                history.push("/");
                            })
                        }
                    >
                        Signout
                    </span>
                </li>
            )}
        </ul>
    </div>
);

export default withRouter(Menu);
